mapper.select:
SELECT *,(SELECT realname FROM users WHERE id=q.assyBy) AS realname,(select realname from users where id=q.assyModifyBy) AS modifyName FROM qdsProductAssy q WHERE 1=1 and q.moduleNo like concat ('%',#{moduleNo},'%') and q.assyNo like concat ('%',#{assyNo},'%') AND q.assyBy IN (SELECT id FROM users WHERE realname LIKE CONCAT ('%',#{realname},'%'))
and DATE_FORMAT(q.assyTime,'%Y-%m-%d') BETWEEN #{dateStart} AND #{dateEnd}
and q.qdsProCategoryId=(select id from qdsProCategory where qdsProCategoryName=#{qdsProCategoryName}) order by id desc limit #{beginNo},#{pageSize}

select count(*) from qdsProductAssy q where 1=1 and q.moduleNo like concat ('%',#{moduleNo},'%') and q.assyNo like concat ('%',#{assyNo},'%') AND q.assyBy IN (SELECT id FROM users WHERE realname LIKE CONCAT ('%',#{realname},'%'))
and DATE_FORMAT(q.assyTime,'%Y-%m-%d') BETWEEN #{dateStart} AND #{dateEnd}
and q.qdsProCategoryId=(select id from qdsProCategory where qdsProCategoryName=#{qdsProCategoryName})

SELECT *,(SELECT realname FROM users WHERE id=q.assyBy) AS realname,(select realname from users where id=q.assyModifyBy) AS modifyName FROM qdsProductAssy q 
WHERE q.qdsProCategoryId=(select id from qdsProCategory where qdsProCategoryName=#{qdsProCategoryName} )
order by assyTime limit #{beginNo},#{pageSize}
